ge vers Djibouti', '', 'inherit', 'closed', 'closed', '', '617-revision-v1', '', '', '2016-12-01 16:05:50', '2016-12-01 15:05:50', '', 617, 'http://localhost:7000/beauval_nature/617-revision-v1/', 0, 'revision', '', 0),
